package Assignments;

public class FindWordEndsWithY {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str="I'm a boy";
		
		if(str.endsWith("y")) {
			
			System.out.println("Word in given string is ends with y");
		}
		
		else {
			System.out.println("Word in given string is not ends with y");
		}
	}

}
